import React, { useState, useRef, useEffect } from 'react';
import './testimonials.css'; // Import CSS file for styling
import Header2 from '../Header/Header2';

const HorizontalInfiniteScroll = ({ data }) => {
  const containerRef = useRef(null);
  const [scrollPosition, setScrollPosition] = useState(0);

  useEffect(() => {
    const container = containerRef.current;

    const handleScroll = () => {
      setScrollPosition(container.scrollLeft);
    };

    container.addEventListener('scroll', handleScroll);
    return () => {
      container.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <>
    
    <Header2></Header2>
    <div
      ref={containerRef}
      className="horizontal-scroll-container    mb-64 " // Apply class for styling
    >
      {data.map((item, index) => (
        <div
          key={index}
          className="scroll-item" // Apply class for styling
        >
          <p>{item.paragraph}</p>
          <h3>{item.heading}</h3>
        </div>
      ))}
    </div>
    </>
  );
};

// Sample data for 10 different objects
const sampleData = [
  { paragraph: "ere are many variations of passages of Lorem Ipsum available, but the majority have suffered alteration in some form, by injected humour, or randomised words which don'as necessary, making this the first true generator o 2", heading: "Trishit Bhowmik" },
  { paragraph: "Paragraph 2", heading: "Heading 2" },
  { paragraph: "Paragraph 3", heading: "Heading 3" },
  { paragraph: "Paragraph 4", heading: "Heading 4" },
  { paragraph: "Paragraph 5", heading: "Heading 5" },
  { paragraph: "Paragraph 6", heading: "Heading 6" },
  { paragraph: "Paragraph 7", heading: "Heading 7" },
  { paragraph: "Paragraph 8", heading: "Heading 8" },
  { paragraph: "Paragraph 9", heading: "Heading 9" },
  { paragraph: "Paragraph 10", heading: "Heading 10" },
];

const App = () => {
  return <HorizontalInfiniteScroll data={sampleData} />;
};

export default App;
