import React, { useState, useEffect, useRef } from 'react';
import './testimonials.css'; // Import your CSS file

const data = [
  { text: 'Item 1 text', title: 'Item 1 title' },
  // ... other data objects
];

const InfiniteScrollHorizontal = ({ data }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const contentRef = useRef(null);

  useEffect(() => {
    // Handle initial scroll position correction
    if (contentRef.current) {
      contentRef.current.scrollLeft = 0;
    }
  }, []);

  const handleScroll = (event) => {
    const scrollWidth = contentRef.current.scrollWidth;
    const clientWidth = contentRef.current.clientWidth;
    const scrollPosition = event.target.scrollLeft;

    // Check if scroll position is near the end of the scrollable area
    if (scrollPosition + clientWidth >= scrollWidth - 50) { // Adjust threshold as needed
      setCurrentIndex((prevIndex) => (prevIndex + 1) % data.length);
    }
  };

  return (
    <div className="infinite-scroll-horizontal" ref={contentRef} onScroll={handleScroll}>
      {data.map((item, index) => (
        <div key={index} className="item">
          <p>{item.text}</p>
          <h3>{item.title}</h3>
        </div>
      ))}
      {/* Duplicate the first element to create seamless looping */}
      <div key="duplicate" className="item">
        <p>{data[0].text}</p>
        <h3>{data[0].title}</h3>
      </div>
    </div>
  );
};

export default InfiniteScrollHorizontal;
