import React, { useState, useRef, useEffect } from 'react';

const HorizontalInfiniteScroll = ({ data }) => {
  const containerRef = useRef(null);
  const [scrollPosition, setScrollPosition] = useState(0);

  useEffect(() => {
    const container = containerRef.current;

    const handleScroll = () => {
      setScrollPosition(container.scrollLeft);
    };

    container.addEventListener('scroll', handleScroll);
    return () => {
      container.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div
      ref={containerRef}
      style={{
        width: '100%',
        overflowX: 'scroll',
        whiteSpace: 'nowrap',
      }}
    >
      {data.map((item, index) => (
        <div
          key={index}
          style={{
            display: 'inline-block',
            minWidth: '300px',
            padding: '20px',
            border: '1px solid #ccc',
            margin: '0 10px',
          }}
        >
          <p>{item.paragraph}</p>
          <h3>{item.heading}</h3>
        </div>
      ))}
    </div>
  );
};

// Sample data for 10 different objects
const sampleData = [
  { paragraph: "Paragraph 1", heading: "Heading 1" },
  { paragraph: "Paragraph 2", heading: "Heading 2" },
  { paragraph: "Paragraph 3", heading: "Heading 3" },
  { paragraph: "Paragraph 4", heading: "Heading 4" },
  { paragraph: "Paragraph 5", heading: "Heading 5" },
  { paragraph: "Paragraph 6", heading: "Heading 6" },
  { paragraph: "Paragraph 7", heading: "Heading 7" },
  { paragraph: "Paragraph 8", heading: "Heading 8" },
  { paragraph: "Paragraph 9", heading: "Heading 9" },
  { paragraph: "Paragraph 10", heading: "Heading 10" },
];

const App = () => {
  return <HorizontalInfiniteScroll data={sampleData} />;
};

export default App;
