import React, { useState, useEffect } from 'react';
import './HorizontalScrollCards.css'; // Import your CSS file for styling

const HorizontalScrollCards = () => {
  const [cards, setCards] = useState([]);

  // Generate some sample cards
  useEffect(() => {
    const sampleCards = Array.from({ length: 5 }, (_, i) => ({
      id: i + 1,
      title: `Card ${i + 1}`,
      description: `This is card ${i + 1}`,
      // Add other properties as needed
    }));
    setCards(sampleCards);
  }, []);

  // Function to handle scrolling
  const handleScroll = (e) => {
    const { scrollLeft, clientWidth, scrollWidth } = e.target;
    if (scrollLeft + clientWidth >= scrollWidth) {
      // Reached the end of scrolling, add more cards
      setCards((prevCards) => {
        const newCards = Array.from({ length: 5 }, (_, i) => ({
          id: prevCards.length + i + 1,
          title: `Card ${prevCards.length + i + 1}`,
          description: `This is card ${prevCards.length + i + 1}`,
          // Add other properties as needed
        }));
        return [...prevCards, ...newCards];
      });
    }
  };

  return (
    <div className="horizontal-scroll-container" onScroll={handleScroll}>
      <div className="cards-wrapper">
        {cards.map((card) => (
          <div key={card.id} className="card">
            <h2>{card.title}</h2>
            <p>{card.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HorizontalScrollCards;
