import React, { useState, useEffect,useRef } from 'react';
import './testimonials.css'; // Import your CSS file for styling



const HorizontalScrollCards = () => {
  const [cards, setCards] = useState([]);
  const scrollRef = useRef(null);

  // Generate sample cards and set initial state
  useEffect(() => {
    const sampleCards = Array.from({ length: 10 }, (_, i) => ({
      id: i + 1,
      title: `Card ${i + 1}`,
      description: `This is a sample description for card ${i + 1}. Lorem ipsum dolor sit amet, consectetur adipiscing elit.`,
      content: `Content ${i + 1}. Proin feugiat suscipit purus nec laoreet. Sed vel ultrices velit.`,
      // Add other properties as needed
    }));
    setCards(sampleCards);
  }, []);

  // Function to handle scrolling
  const handleScroll = () => {
    const { scrollLeft, clientWidth, scrollWidth } = scrollRef.current;
    if (scrollLeft + clientWidth === scrollWidth) {
      // Reached the end of scrolling, reset to the beginning
      const newCards = cards.slice(5).concat(cards.slice(0, 5));
      setCards(newCards);
    }
  };

  return (
    <div className="horizontal-scroll-container" ref={scrollRef} onScroll={handleScroll}>
      <div className="cards-wrapper">
        {cards.map((card) => (
          <div key={card.id} className="card">
            <h3>{card.title}</h3>
            <p>{card.description}</p>
            <h4>{card.content}</h4>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HorizontalScrollCards;
