import React, { useState, useRef, useEffect } from 'react';
import './testimonials.css'; // Import CSS file for styling
import Header2 from '../Header/Header2';
import Footer from '../Footer/Footer';
const HorizontalInfiniteScroll = ({ data }) => {
  const containerRef = useRef(null);
  const [scrollPosition, setScrollPosition] = useState(0);

  useEffect(() => {
    const container = containerRef.current;

    const handleScroll = () => {
      setScrollPosition(container.scrollLeft);
    };

    container.addEventListener('scroll', handleScroll);
    return () => {
      container.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <>
    
    <Header2></Header2>
    <div>
      <h1 className='text-5xl ml-80 text-bold font-bold text-orange-500'>OUR  HAPPY  CUSTOMERS</h1>
    </div>
    <div
      ref={containerRef}
      className="horizontal-scroll-container  mb-16 " // Apply class for styling
    >
      {data.map((item, index) => (
        <div
          key={index}
          className="scroll-item" // Apply class for styling
        >
          <p>{item.paragraph}</p>
          <h3>{item.heading}</h3>
        </div>
      ))}
    </div>
    <Footer></Footer>
    </>
  );
};

